PRAGMA defer_foreign_keys=TRUE;
DROP TABLE IF EXISTS Cvs;
CREATE TABLE Cvs (CvID INTEGER PRIMARY KEY AUTOINCREMENT, FullName TEXT, ImgDataUrl TEXT, Position TEXT, Email TEXT, LinkedIn TEXT, Phone TEXT, About TEXT, Education TEXT, Projects TEXT, Skills TEXT, Certificates TEXT, WorkExperience TEXT, UserID TEXT, "ImgVersion" TEXT DEFAULT 1);

DROP TABLE IF EXISTS Roles;
CREATE TABLE IF NOT EXISTS "Roles" ( 	"id"	INTEGER, 	"Name"	TEXT NOT NULL UNIQUE, 	PRIMARY KEY("id" AUTOINCREMENT) );

DROP TABLE IF EXISTS Users;
CREATE TABLE IF NOT EXISTS "Users" ( 	"UserID"	INTEGER, 	"UserEmail"	TEXT, 	"UserPassword"	TEXT, 	"UserRole"	INTEGER, 	PRIMARY KEY("UserID" AUTOINCREMENT) );
